package com.example.day06;

public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] ary1 = new int[] {1,2,3,4,5};
		for(int i =0; i<ary1.length; i++) {
		System.out.println(ary1[i]);
		}
		
		
		
		for(int number : ary1) {
			System.out.println(number);
		}
		
		
		
		
	}
		
}
